import styled from 'styled-components/macro';

export const Container = styled.div`
    display: flex;
    margin-top: 30px;
    justify-content: center;
    align-items: center;
`
export const Bigtext = styled.div`
    font-weight: bold;
    font-size: 30px;
    color: red;
`

export const LoadingGif = styled.img`
    height: 40px;
    width: 60px;
    margin-right: 40px;
    border-style: solid;

    @media (min-width: 1000px) {
    height: 40px;
    width: 60px;
`
