import styled, {keyframes}from 'styled-components/macro';
import { Link as ReactRouterLink } from 'react-router-dom';


export const Title = styled.p`
  font-size: 24px;
  color: #000000;
  font-weight: bold;
  margin-left: 56px;
  margin-right: 56px;
  margin-top: 0;
`;

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 50px;

  > ${Title} {
    @media (max-width: 1000px) {
      margin-left: 30px;
    }
  }

  &:last-of-type {
    margin-bottom: 0;
  }
`;

export const Link = styled(ReactRouterLink)`
      color: #000000;
      text-decoration: none;
    margin-right: 30px;
    font-weight: ${({ active }) => (active === 'true' ? '700' : 'normal')};
    cursor: pointer;

    &:hover {
        font-weight: bold;
    }
    &:last-of-type {
        margin-right: 0;
      }
`;

export const Text = styled.div`
  margin-top: 5px;
  font-size: 10px;
  color: #000000;
  margin-bottom: 0;
  user-select: none;
  line-height: normal;
`;

export const BorderCon = styled.div`
  border: 1px solid #ccc;
  padding: 0.01em 16px;
  border-radius: 16px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-top: 30px;
  margin-bottom: 30px;
  max-width: 400px;
`;

export const Meta = styled.div`
  bottom: 0;
  padding: 10px;
  margin-bottom: 30px;
`;
