import { createContext, useContext, useEffect, useMemo, useState } from 'react';
import { useLocation, useNavigate } from "react-router-dom";
import axios from 'axios';


const AuthContext = createContext(null);

export function AuthProvider({ children }) {
    const [user, setUser] = useState();
    const [error, setError] = useState();
    const [loading, setLoading] = useState(false);
    const [isLogin, setisLogin] = useState(false);

    const location = useLocation();
    const history = useNavigate();

    useEffect(() => {
        if (error) setError(null);
      }, [location.pathname]);

    useEffect(() => {
        async function verifyLogin() {
            localStorage.getItem('token');
            let res = await axios.get(`${process.env.REACT_APP_BACKEND_URI}/welcome`,{
                headers: {
                    'x-access-token' : token,
                    'Accept' : 'application/json',
                    'Content-Type' : '*/*',
                    'User-Agent' : 'Client',
                    'Connection' : 'keep-alive',
                    'Host' : 'localhost'
                }
            })
            .then(res => {
                if (res.username && res.token) {
                    setUser({...res}); 
                }
                localStorage.setItem('token', user.token);
                setisLogin(true);
            })
            .catch((error) => {
                setError(error);
                localStorage.removeItem('token');
                setUser(null);
                setisLogin(false);
            })
        }

        verifyLogin();
    })

    const signUp = async (email, username, password) => {
        setLoading(true);

        let res = await axios.post(`${process.env.REACT_APP_BACKEND_URI}/register`, {
                "username": username,
                "email" : email,
                "password": password
        },{
            headers: {
                'Accept' : 'application/json',
                'Content-Type' : '*/*',
                'User-Agent' : 'Client',
                'Connection' : 'keep-alive',
                'Host' : 'localhost'
            }
        })
        .then((res) => {
            if (res.username && res.token) {
                setUser({...res}); 
            }
            localStorage.setItem('token', user.token);
            setisLogin(true);
        })
        .catch((error) => {
            setError(error);
            setUser(null);
            setisLogin(false);
        })
        .finally(() => setLoading(false));
    }

    const login = async (username, password) => {
        setLoading(true);

        let res = await axios.post(`${process.env.REACT_APP_BACKEND_URI}/login`, {
                "username": username,
                "password": password
        },{
            headers: {
                'Accept' : 'application/json',
                'Content-Type' : '*/*',
                'User-Agent' : 'Client',
                'Connection' : 'keep-alive',
                'Host' : 'localhost'
            }
        })
        .then((res) => {
            if (res.username && res.token) {
                setUser({...res}); 
            }
            localStorage.setItem('token', user.token);
            setisLogin(true);
            history("/browse");
        })
        .catch((error) => {
            setError(error);
            setUser(null);
            setisLogin(false);
        })
        .finally(() => setLoading(false));
    }


    const memoedVal = useMemo(() => ({
        user,
        error,
        loading,
        isLogin,
        login,
        signUp
    }),
    [user, loading, error])

    return (
        <AuthContext.Provider value = {memoedVal}>
            {children}
        </AuthContext.Provider>
    )
}

export default function useAuth() {
    return useContext(AuthContext);
}
