import styled, {keyframes}from 'styled-components/macro';
import { Link as ReactRouterLink } from 'react-router-dom';

export const Background = styled.div`
    display: flex;
    flex-direction : column;
    background: white;
    
    @media (max-width: 1100px) {
      ${({dontShowOnSmallViewPort}) => dontShowOnSmallViewPort && `background: none;`}
    }
`;

export const Container = styled.div`
      display: flex; 
      margin: 0 60px;
      height: 150px;
      align-items: center;
      justify-content: space-between;
      box-shadow: 0px 6px 2px -2px #aaa;
      margin-bottom: 30px;
      

      a {
        display: flex;
      }
    

      @media (max-width: 1000px) {
        margin: 0 30px;
      }
`;

export const Textlink = styled.p`
    color: #000000;
    text-decoration: none;
    margin-right: 30px;
    font-weight: ${({ active }) => (active === 'true' ? '700' : 'normal')};
    cursor: pointer;

    &:hover {
        font-weight: bold;
    }
    &:last-of-type {
        margin-right: 0;
      }
`;

export const Link = styled(ReactRouterLink)`
      color: #000000;
      text-decoration: none;
    margin-right: 30px;
    font-weight: ${({ active }) => (active === 'true' ? '700' : 'normal')};
    cursor: pointer;

    &:hover {
        font-weight: bold;
    }
    &:last-of-type {
        margin-right: 0;
      }
`;


export const Group = styled.div`
    display: flex;
    align-items: right;
    min-width: calc(60% - 20px);
    margin-right: 20px;
    justify-self: flex-end;
`;

export const Dropdown = styled.div`
display: none;
position: absolute;
background-color: white;
padding: 10px;
width: 100px;
top: 32px;
right: 10px;

${Group}:last-of-type ${Link} {
  cursor: pointer;
}

${Group} {
  margin-bottom: 10px;

  &:last-of-type {
    margin-bottom: 0;
  }

  ${Link} {
    cursor: pointer;
  }

}

button {
  margin-right: 10px;
}

p {
  font-size: 12px;
  margin-bottom: 0;
  margin-top: 0;
}
`;

export const Profile = styled.div`
      display:flex;
      align-items: center;
      position: relative;
    
      button {
        cursor: pointer;
      }
      &:hover > ${Dropdown} {
        display: flex;
        flex-direction: column;
      }

      &:hover > ${Link} {
        font-weight: bold;
      }
`;

export const Account = styled.div`
      display:flex;
      align-items: flex-end;
      margin-left: 20px;
      position: relative;
    
      button {
        cursor: pointer;
      }
      &:hover > ${Dropdown} {
        display: flex;
        flex-direction: column;
      }

      &:hover > ${Link} {
        font-weight: bold;
      }
`;

export const Icon = styled.img`
height: 40px;
width: 60px;
margin-right: 40px;
border-style: solid;

@media (min-width: 1000px) {
  height: 40px;
  width: 60px;
}
`;

export const Logo = styled.img`
height: 40px;
width: 60px;
margin-right: 40px;
justify-self: flex-start;

@media (min-width: 1449px) {
  height: 40px;
  width: 60px;
}
`;

const ShineAni = keyframes`
to {
    background-position: 200% center;
  }
`;

export const ButtonLink = styled(ReactRouterLink)`
  display: block;
  background: #900;
  width: 90px;
  height: fit-content;
  color: white;
  border: 0;
  font-size: 15px;
  border-radius: 3px;
  padding: 8px 17px;
  cursor: pointer;
  text-decoration: none;
  justify-self: flex-start;
`;

export const UserImg = styled.div`
  width: 40px;
  height: 40px;
  position: relative;
  overflow: hidden;
  border-radius: 50%;
  text-align: center;
  cursor: pointer;
`;

export const User = styled.img`
    display: inline;
    margin: 0 auto;
    height: 100%;
    width: auto;
`;
