import styled, {keyframes}from 'styled-components/macro';

export const Container = styled.div`
    max-height: 1500px;
    width: 100%;
    display: flex;
    align-item: center;
    justify-content: center;
    flex-direction: column;
`;

export const PicCon = styled.div`
    display: flex;
    min-height: 500px;
    margin-top: 100px;
    width: 100%;
    aligned-item: center;
    justify-content: center;
`;

export const PicLeft = styled.img`
    width: 30%;
    object-fit: cover;
    height: 300px;
    transform: translate(20px,120px);
    z-index: 10;
`;

export const PicRight = styled.img`
    width: 30%;
    object-fit: cover;
    max-width: 500px;
    height: 300px;
    transform: translateX(-20px);
`;

export const Title = styled.p`
  text-align: center;
  color: #000000;
  font-weight: bold;
  font-family: Arvo-Bold;
  font-size: 34px;
  max-width: 350px;
  align-self:center;
`;

export const TextBlock = styled.p`
  text-align: center;
  font-size: 16px;
  max-width: 700px;
  align-self:center;
`;

export const ProfileContainer  = styled.div`
  display: flex;
  min-height: 300px;
  width: 100%;
  aligned-item: center;
  justify-content: center;
`;

export const Profile = styled.div`
  width: 150px;
  padding: 30px 10px;
  border-top: 4px solid #888888;
`
