import React, {useState} from "react";
import HeadCon from "../containers/header";
import { useParams } from "react-router-dom";
import { Profile } from "../component";
import { Column } from "../component/profile/styles/profile";
import useContent from '../hook/use-content';
import Buffer from "buffer"
import { useEffect } from "react";
import { PostApi, GetApi } from "../hook/use-api";
import Loading from "../containers/loading";
window.Buffer = window.Buffer || require("buffer").Buffer; 


export default function ProfilePage() {
    const {userid} = useParams();

    const [user, SetUser] = useState({});
    
    useEffect(() => {
        console.log("running");
        let header = {
            'Accept' : 'application/json',
            'Content-Type' : 'application/json',
        }
        let params = {}
        let payload =  async () => {
            let data = await GetApi(`user/${userid}`,params,header);
            //console.log(data);
            return SetUser(data);
        }

        payload();
    },[]);



    return (
        <>
                <HeadCon/>
                <Profile>
                    <Profile.Column>
                        <Profile.ProfileUser>
                            <Profile.ImageCrop>
                                <Profile.Image src={user?.img ? `data:${user.img.contentType};base64,${window.Buffer.from(user.img.data.data).toString('base64')}` : process.env.PUBLIC_URL + '/image/default.jpg'}/>
                            </Profile.ImageCrop>
                        </Profile.ProfileUser>
                        <Profile.Text Bold='true' Margin='15px'>{user?.username ? user.username : "username"}</Profile.Text>
                        <Profile.Text>{user?.email ? user.email : "email@email.com"}</Profile.Text>
                    </Profile.Column>
                    { user?.username ? <Profile.Column>
                        <Profile.Text Bold='true' Margin='0px 50px' FontSize='35px'></Profile.Text>
                        <Profile.Text>
                        Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.

                        </Profile.Text>
                    </Profile.Column> : <Loading/>}
                    
                </Profile>
        </>
    )
}
