import styled, {keyframes}from 'styled-components/macro';

export const Container = styled.div`
    max-height: 1500px;
    width: 100%;
    display: flex;
    align-item: center;
    justify-content: center;
    flex-direction: column;
`;

export const PicCon = styled.div`
    display: flex;
    min-height: 500px;
    margin-top: 100px;
    width: 100%;
    aligned-item: center;
    justify-content: center;
`;

export const PicLeft = styled.img`
    width: 30%;
    object-fit: cover;
    height: 300px;
    transform: translate(20px,120px);
    z-index: 10;
`;

export const PicRight = styled.img`
    width: 30%;
    object-fit: cover;
    max-width: 500px;
    height: 300px;
    transform: translateX(-20px);
`;

export const Title = styled.p`
  text-align: center;
  color: #000000;
  font-weight: bold;
  font-family: Arvo-Bold;
  font-size: 34px;
  max-width: 350px;
  align-self:center;
`;

export const TextBlock = styled.p`
  text-align: center;
  font-size: 16px;
  max-width: 700px;
  align-self:center;
`;

export const ProfileContainer  = styled.div`
  display: flex;
  flex-direction: row;
  min-height: 300px;
  width: 100%;
  align-items: flex-start;
  justify-content: center;
`;

export const Profile = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 145px;
  border: red;
  overflow: hidden;
  border-radius: 10px;
  margin: 0 10px;
`
export const Link = styled.a`
  width: 170px;
  text-align: center;
  cursor: pointer;
  aligned-item: flex-start;
  color: #FFFFFF;
  text-decoration: none;
  background: #900;
  height: 30px;
`
export const Image = styled.img`
  width: 130px;
  padding: 10px 10px;
  overflow: hidden;
  display: inline;
`
