import styled from "styled-components";

export const SearchContainer = styled.div`
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 100%;
    height: 100vh;
    text-align: center;
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 2rem;
`;

export const SearchTitle = styled.div`
    font-family: Avro;
    font-size: 27px;
`

export const SearchForm = styled.form`
    display: flex;
    align-items: center;
    position: relative;
    width: 50%;
    margin: 0 auto;
`;

export const SearchBar = styled.input`
    width: 100%;
    padding: 1rem 2rem;
    border-radius: 20%;
    border: none;
    font-size: 1.6rem;
    transition: all 0.5s ease;
    box-shadow: 0 0 10px #d9d9d9;
    &:focus {
        width: 70%;
        outline: none;
        box-shadow: 0 0 27px #d9d9d9;
    }
    &:hover{
        box-shadow: 0 0 20px #d9d9d9;
    }
`;

export const SearchButton = styled.button`
    position: absolute;
    right: 1rem;
    border: none;
    background-color: transparent;
    font-size: 2rem;
    transition: all 0.3s ease;
    &:hover {
        transform: scale(1.2);
        cursor: pointer;
    }
`;
