import styled from "styled-components";

export const SearchContainer = styled.div`
position: relative;
display: flex;
align-items: center;
justify-content: center;
width: 100%;
height: 100vh;
`;

export const SearchBar = styled.input`
    width: 50%;
    padding: 1rem 2rem;
    border-radius: 1rem;
    border: none;
    font-size: 1.6rem;
    transition: all 0.5s ease;
    &:focus {
        width: 70%;
        outline: none;
    }
`;

export const SearchButton = styled.button`
    position: absolute;
    right: 1rem;
    border: none;
    background-color: transparent;
    font-size: 2rem;
    transition: all 0.3s ease;
    &:hover {
        transform: scale(1.2);
        cursor: pointer;
    }
`;
