import React from 'react';
import { Route, Redirect } from 'react-router-dom';

export function IsUserRedirect({ isLogin, loggedInPath, children, ...rest }) {
  return (
    <Route
      {...rest}
      render={() => {
        if (!isLogin) {
          return children;
        }

        if (isLogin) {
          return (
            <Redirect
              to={{
                pathname: loggedInPath,
              }}
            />
          );
        }

        return null;
      }}
    />
  );
}

export function ProtectedRoute({ isLogin, children, ...rest }) {
  return (
    <Route
      {...rest}
      render={({ location }) => {
        if (isLogin) {
          return children;
        }

        if (!isLogin) {
          return (
            <Redirect
              to={{
                pathname: 'signin',
                state: { from: location },
              }}
            />
          );
        }

        return null;
      }}
    />
  );
}
