import React, {useState} from "react";
import HeadCon from "../containers/header";
import { useParams } from "react-router-dom";
import { Profile } from "../component";
import { Column } from "../component/profile/styles/profile";
import useContent from '../hook/use-content';
import { useEffect } from "react";
import { PostApi, GetApi } from "../hook/use-api";
import Loading from "../containers/loading"


export default function ProfilePage() {
    const {userid} = useParams();

    const [user, SetUser] = useState({});
    
    useEffect(() => {
        let header = {
            'Accept' : 'application/json',
            'Content-Type' : 'application/json',
        }
        let params = {}
        let payload =  async () => {
            let data = await GetApi(`user/${userid}`,params,header);
            //console.log(data);
            return SetUser(data);
        }
        
        console.log(content);
    },[]);



    return (
        <>
                <HeadCon/>
                <Profile>
                    <Column>
                        <Profile>
                            <ImageCrop>
                                <Image src={user?.img ? `data:${user.img.contentType};base64,${Buffer.from(user.img.data.data).toString('base64')}` : process.env.PUBLIC_URL + '/image/default.jpg'}/>
                            </ImageCrop>
                        </Profile>
                        <Text Bold='true' Margin='15px'>{user?.username ? user.username : "username"}</Text>
                        <Text>{user?.email ? user.email : "email@email.com"}</Text>
                    </Column>
                    { user?.username ? <Column>
                        <Text Bold='true' Margin='30px' FontSize='35px'></Text>
                        <Text></Text>
                    </Column> : <Loading/>}
                    
                </Profile>
        </>
    )
}
